styles:
body {
   color: #455469;
   text-align: center;
   font-family: 'Open Sans';
   width: 400px;
   margin: auto;
}
 td {
   text-decoration: none;
   color: white;
   background: #155C93;
   font-style: normal;
   font-weight: 700;
   font-size: 14px;
   line-height: 22px;
   border-radius: 3px;
   padding: 15px;
}
 .content {
   background-color: #fff;
   border-radius: 4px;
}
 h2 {
   margin: 50px;
}
 h3 {
   text-align: left;
}
 h4 {
   font-style: normal;
   font-weight: 400;
}
 h5 {
   color: #666666;
   font-family: 'Inter';
   font-style: normal;
   font-weight: 400;
   font-size: 16px;
   line-height: 24px;
}
 h6 {
   color: #999999;
   font-family: 'Inter';
   -font-style: normal;
   font-weight: 400;
   font-size: 12px;
   line-height: 16px;
}
 table {
   margin: auto;
   background-color: #dedddc;
}
 tr {
   background-color: #dedddc;
}

a {
  text-decoration: none;
  color: white;
}
